function def.other()
cecho("\n<green>Other:")
def.num = 3

if def.table.other.preach then
	def.yes("Preach Blessing")
end
if def.table.other.barrier then
  def.yes("Prismatic Barrier")
end
if def.table.other.blind then
  def.yes("Blind")
end
if def.table.other.deaf then
  def.yes("Deaf")
end
--Weaponry
if def.class ~= "Knight" and def.class ~= "Blademaster" then
  if table.contains(def.skills.weaponry, "Targeting") then
    if def.table.other.target then
      def.yes("Target (<green>"..def.table.other.target.."<white>)")
    else
      def.no("Target")
    end
  end
  if table.contains(def.skills.weaponry, "Parrying") then
    if def.table.other.parry then
      def.yes("Parry (<green>"..def.table.other.parry.."<white>)")
    else
      def.no("Parry")
    end
  end
end
--other
if def.table.other.phased then
	def.yes("Phased")
end
if def.table.other.mounted then
	def.yes("Mounted ("..def.table.other.mounted..")")
end
if def.table.other.flight then
	def.yes("Flying")
end
if def.table.other.shipwarning then
  def.yes("Shipwarning")
end
if def.table.other.healthbless and def.table.other.manabless and def.table.other.egobless then
	def.yes("Vitals Blessing")
else
	if def.table.other.healthbless then
		def.yes("Health Blessing")
	end
	if def.table.other.manabless then
		def.yes("Mana Blessing")
	end
	if def.table.other.egobless then
		def.yes("Ego Blessing")
	end
end
if def.table.other.dhealthbless and def.table.other.dmanabless and def.table.other.degobless then
	def.yes("Divine Vitals Blessing")
else
	if def.table.other.dhealthbless then
		def.yes("Divine Health Blessing")
	end
	if def.table.other.dmanabless then
		def.yes("Divine Mana Blessing")
	end
	if def.table.other.degobless then
		def.yes("Divine Ego Blessing")
	end
end
if def.table.other.boost then
	def.yes(def.table.other.boost.. "% Experience Boost")
end
if def.table.other.critAmt then
  if def.table.other.critTime then
    def.yes(def.table.other.critAmt.. "% Crit Bonus "..def.table.other.critTime)
  else
    def.yes(def.table.other.critAmt.. "% Crit Bonus ")
  end
end
if def.table.other.expBonus then
  def.yes("Exp Bonus (<green>"..def.table.other.expBonus.."<white>)")
end
if def.table.other.cavalier then
	for i = 1, #def.table.other.cavalier, 2 do
		def.yes(def.table.other.cavalier[i].." Resist ("..def.table.other.cavalier[i+1]..")")
	end
end
if def.table.other.crotamine then
	def.yes("Crotamine Immunity")
end
if def.table.other.poisonimmune then
	def.yes(def.table.other.poisonimmune.." Immunity")
end
--racial
if def.table.other.enrage then
	def.yes("Enraged")
end
if def.table.other.innerserpent then
	def.yes("Inner Serpent ("..def.table.other.innerserpent..")")
end
if def.table.other.bask then
	def.yes("Basking")
end
--shrine powers
if def.table.other.celerity then
	def.yes("Celerity")
end
if def.table.other.shrinecure then
	def.yes("Shrine Cure")
end
if def.table.other.shrineheal then
	def.yes("Shrine Heal")
end
if def.table.other.shrineprotect then
	def.yes("Shrine Protection")
end
if def.table.other.vendetta then
	def.yes("Shrine Vendetta")
end
if def.table.other.gravity then
	def.yes("Shrine Gravity")
end
--quests

--from other people's skills
if def.table.other.teacher then
	def.yes("Teacher ("..def.table.other.teacher..")")
end

if def.table.zeal.will and def.table.zeal.endure and def.table.zeal.thermal and def.table.zeal.frost and def.table.zeal.protect then
  def.yes("Benediction")
else
  if def.table.zeal.will then
  	def.yes("Willpower regeneration")
  end
  if def.table.zeal.endure then
  	def.yes("Endurance regeneration")
  end
  if def.table.zeal.thermal then
  	def.yes("Thermal Spiritshield")
  end
  if def.table.zeal.frost then
  	def.yes("Frost Spiritshield")
  end
