function def.dragon()
cecho("\n<green>Dragon:")
def.num = 3

  if def.table.dragon.armor then
    def.yes("Armor")
  else
    def.no("Armor")
  end
  if def.table.dragon.breath then
    def.yes("Breath")
  else
    def.no("Breath")
  end
	if def.table.dragon.block then
		def.yes("Blocking (<green>"..def.table.dragon.block.."<white>)")
	else
		def.no("Blocking")
	end
end

function def.forestal()
  if gmcp.Char.Status.class == "Sylvan" then
    cecho("\n<green>Propagation:")
    def.num = 3
    if def.table.forest.viridian then
      def.yes("Viridian")
      if def.table.prop.head then
        def.yes("Head (<green>"..def.table.prop.head.."<white>)")
      else
        def.no("Head")
      end
      if def.table.prop.body then
        def.yes("Body (<green>"..def.table.prop.body.."<white>)")
      else
        def.no("Body")
      end
      if def.table.prop.arms then
        def.yes("Arms (<green>"..def.table.prop.arms.."<white>)")
      else
        def.no("Arms")
      end
      if def.table.prop.legs then
        def.yes("Legs (<green>"..def.table.prop.legs.."<white>)")
      else
        def.no("Legs")
      end
    else
      def.no("Viridian")
    end
    if table.contains(def.skills.propagation, "Barkskin") then
      if def.table.forest.barkskin then
        def.yes("Barkskin")
      else
        def.no("Barkskin")
      end
    end
  end
  --Probably put weatherweaving here
  --Then metamorphosis
  if gmcp.Char.Status.class == "Sylvan" or gmcp.Char.Status.class == "Druid" then
    cecho("\n<green>Groves:")
    def.num = 3
    if table.contains(def.skills.groves, "Quarterstaff") then
      if def.table.forest.spin then
        def.yes("Spin staff")
      elseif def.table.forest.flail then
        def.yes("Flail staff")
      else
        def.no("Staff")
      end
    end
  end
end

