function def.tables()

if def.class == "Knight" then
  def.table.knight = {}
end

if def.class == "Elemental" then
  def.table.element = {}
end

if def.class == "Magi" then
  def.table.channels = def.table.channels or {}
  def.table.magi = {}
end

if def.class == "Priest" then
  def.table.priest = {}
end

if def.class == "Dragon" then
  def.table.dragon = {}
end

if def.class == "Forestal" then
  def.table.forest = {}
  if gmcp.Char.Status.class == "Sylvan" then
    def.table.prop = def.table.prop or {}
    if def.skills.weatherweaving then
      def.table.channels = def.table.channels or {}
    end
  end
end

if def.class == "Blademaster" then
  def.table.knight = {}
  def.table.blade = {}
end

if def.class == "Shaman" then
  def.table.shaman = {}
  def.table.spirits = def.table.spirits or {}
end

  def.table.rune = def.table.rune or {}
  def.table.zeal = {}
	def.table.tattoos = {}
  def.table.vision = {}
	def.table.enchantments = {}
	def.table.other = {}
	def.table.consume = {}
	def.table.item = {}
end
