Trigger.script:
def.table.astro = def.table.astro or {}
table.insert(def.table.astro, matches[2])
deleteLine()

def.table.domoth = def.table.domoth or {Lesser = {},Minor = {},Major = {}}
def.table.domoth[matches[3]]["Chaos"] = string.title(matches[2])
table.insert(def.table.domoth[matches[3]], "Chaos")

deleteLine()

Script.script:
def = def or {table = {}, skills = {}}

function show_defs()
if def.day == nil then
	send("time raw")
end

if def.class == "Forestal" then
	def.nature()
	def.totems()
	if def.skills.healing then
		def.healing()
	elseif def.skills.hexes then
		def.hexes()
	elseif def.skills.runes then
		def.runes()
	elseif def.skills.hunting then
		def.hunting()
