end

if def.class == "Guardian" then
	def.cosmic()
	def.rituals()
	if def.skills.healing then
		def.healing()
	elseif def.skills.hexes then
		def.hex()
	elseif def.skills.tarot then
		def.tarot()
	end
	def.highmagic()
end

if def.class == "Monk" then
