function def.spacer()
	if def.num == 3 then
		def.num = 0
		echo("\n   ")
	end
	def.num = def.num + 1
end

function def.yes(name)  
	def.spacer()
  local str = string.gsub(name,"<%w+>","")
	if string.len(str) > 18 then
    if string.len(str) < string.len(name) then
      local num = string.len(name) - string.len(str)
      cecho("<white>[<green>X<white>] "..string.format("%-"..(40+num).."s",name))
    else
		--name = string.cut(name, 40)
		  cecho("<white>[<green>X<white>] "..string.format("%-40s",name))
    end
    if def.num ~= 3 then
  		def.spacer()
    end
	else
    if string.len(str) < string.len(name) then
      local num = string.len(name) - string.len(str)
      cecho("<white>[<green>X<white>] "..string.format("%-"..(18+num).."s",name))
    else
  		cecho("<white>[<green>X<white>] "..string.format("%-18s",name))
    end
	end
end

function def.mana(name)  
	def.spacer()
  local str = string.gsub(name,"<%w+>","")
	if string.len(str) > 18 then
		name = string.cut(name, 40)
		cecho("<white>[<green>M<white>] "..string.format("%-40s",name))
    if def.num ~= 3 then
  		def.spacer()
    end
	else
    if string.len(str) < string.len(name) then
      local num = string.len(name) - string.len(str)
      cecho("<white>[<green>M<white>] "..string.format("%-"..(18+num).."s",name))
    else
  		cecho("<white>[<green>M<white>] "..string.format("%-18s",name))
    end
	end
end

function def.no(name)
	def.spacer()
  local str = string.gsub(name,"<%w+>","")
  if string.len(str) > 18 then
		name = string.cut(name, 40)
		cecho("<white>[ ] "..string.format("%-40s",name))
    if def.num ~= 3 then
  		def.spacer()
    end
	else
    if string.len(str) < string.len(name) then
      local num = string.len(name) - string.len(str)
      cecho("<white>[ ] "..string.format("%-"..(18+num).."s",name))
    else
  		cecho("<white>[ ] "..string.format("%-18s",name))
    end
  end
end

function def.dormant(name)
	def.spacer()
  local str = string.gsub(name,"<%w+>","")
  if string.len(str) > 18 then
		name = string.cut(name, 40)
		cecho("<white>[<yellow>X<white>] "..string.format("%-40s",name))
    if def.num ~= 3 then
  		def.spacer()
    end
	else
    if string.len(str) < string.len(name) then
      local num = string.len(name) - string.len(str)
      cecho("<white>[<yellow>X<white>] "..string.format("%-"..(18+num).."s",name))
    else
  		cecho("<white>[<yellow>X<white>] "..string.format("%-18s",name))
    end
  end
end

