function def.zarakido()
cecho("\n<green>Zarakido:")
def.num = 3
if table.contains(def.skills.zarakido, "InfusedWill") then
	local color = "red"
	if def.table.zarakido.type == "harmoniously" then
		color = "cyan"
	elseif def.table.zarakido.type == "chaotically" then
		color = "orange"
	end
	if def.table.zarakido.infused then
		def.yes("Infused Will (<"..color..">"..def.table.zarakido.infused.."<white>)")
	elseif def.table.zarakido.awakened then
		def.yes("Awakened Will (<"..color..">"..def.table.zarakido.awakened.."<white>)")
	elseif def.table.zarakido.dolorous then
		def.yes("Dolorous Will")
	elseif def.table.zarakido.nomadic then
		def.yes("Nomadic Will")
	elseif def.table.zarakido.intimidate then
		def.yes("Intimidating Will ("..def.table.zarakido.intimidate..")")
	elseif def.table.zarakido.precog then
		def.yes("Precognitive Will")
	elseif def.table.zarakido.hunger then
		def.yes("Hungering Will")
	elseif def.table.zarakido.peerless then
		def.yes("Peerless Will")	else
		def.no("Will")
	end
end
end

