	def.yes(def.table.construct.name)
	if def.table.construct.name == "Nightwraith" then
		if def.table.construct.garb then
			def.yes("Garb")
		else
			def.no("Garb")
		end
	end
	if def.table.construct.name == "Lich" then
		if def.table.construct.bloodrage then
			def.yes("Bloodrage")
