function def.archetype()
if def.skills.nature then def.class = "Forestal" elseif def.skills.kata then def.class = "Monk" elseif def.skills.knighthood then def.class = "Warrior" elseif def.skills.elementalism then def.class = "Mage" elseif def.skills.music then def.class = "Bard" elseif def.skills.cosmic then def.class = "Guardian" else def.class = "None" end
end

function def.tables()

if def.class == "Forestal" then
	def.table.nature = {}
	def.table.totems = def.table.totems or {}
	if def.skills.moon then
		def.table.moon={shine = "none"}
	elseif def.skills.night then
		def.table.night = {}
	elseif def.skills.stag then
		def.table.stag = {}
	elseif def.skills.crow then
		def.table.crow = {}
	end
	if def.skills.wicca then
		def.table.wicca = def.table.wicca or {}
	end
	if def.skills.druidry then
		def.table.druid = {}
	elseif def.skills.wildewood then
		def.table.wildewood = {}
	end
	if def.skills.hexes then
		def.table.hex = {}
	elseif def.skills.healing then
		def.table.heal = {}
	elseif def.skills.runes then
		def.table.runes = {}
	elseif def.skills.hunting then
		def.table.hunt = {}
	end
end

if def.class == "Mage" then
	def.table.element = {}
	if def.skills.aerochemantics or aquachemantics then
		def.table.chemantics = def.table.chemantics or {field = {}}
	end
	def.table.illusions = {}
	if def.skills.psionics then
		def.table.psi = {}
	elseif def.skills.runes then
		def.table.runes = {}
	end
end

if def.class == "Guardian" then
	def.table.cosmic = {}
	if def.skills.transmology then
		def.table.transm = def.table.transm or {}
	elseif def.skills.harmonics then
		def.table.gems = def.table.gems or { spin = {}, active = {} }
	end
	def.table.ritual = {}
	if def.skills.healing then
		def.table.heal = {}
	elseif def.skills.hexes then
		def.table.astro = {}
	elseif def.skills.tarot then
		def.table.tarot = {}
	end
end

if def.class == "Monk" then
	def.table.kata = {}
	if def.skills.harmony then
		def.table.harmony = {}
	elseif def.skills.stealth then
		def.table.stealth = {}
	elseif def.skills.zarakido then
		def.table.zarakido = {}
	end
	if def.skills.acrobatics then
		def.table.acro = {}
	elseif def.skills.psionics then
		def.table.psi = {}
	end
end

if def.class == "Bard" then
	def.table.music = {}
	def.table.acro = {}
	if def.skills.illusions then
		def.table.illusions = {}
	elseif def.skills.tarot then
		def.table.tarot = {}
	elseif def.table.hunting then
		def.table.hunt = {}
	end
end

if def.class == "Warrior" then
	def.table.knight = {}
	def.table.athlete = {}
	if def.skills.rituals then
		def.table.ritual = {}
	elseif def.skills.totems then
		def.table.totems = def.table.totems or{}
		if def.skills.moon then
			def.table.moon={shine = "none"}
		elseif def.skills.night then
			def.table.night = {}
		elseif def.skills.stag then
			def.table.stag = {}
		elseif def.skills.crow then
			def.table.crow = {}
		end
	elseif def.skills.hunting then
		def.table.hunt = {}
	end
end

if def.skills.highmagic then
	def.table.highmagic = {}
elseif def.skills.lowmagic then
	def.table.lowmagic = {}
end

	def.table.construct = {active=false}
	def.table.enchantments = {}
	def.table.other = {}
	def.table.consume = {}
	def.table.item = {}
	def.table.drama = {}
	def.table.astro = nil
	def.table.domoth = nil
end
