Script.script:
function wm.aether.gridheal()
if wm.aether.clarity and wm.aether.haveClarity then send("grid clarity "..wm.aether.empath.table.chair[1]) end send("grid repair hull", false) send("grid repair module "..wm.aether.empath.table.grid[1], false) send("grid repair module "..wm.aether.empath.table.chair[1], false) for key in pairs(wm.aether.empath.table.turrets) do send("grid repair module "..wm.aether.empath.table.turrets[key], false) end if #wm.aether.empath.table.cube > 0 then send("grid repair module "..wm.aether.empath.table.cube[1], false) end if #wm.aether.empath.table.collectors > 0 then for key in pairs(wm.aether.empath.table.collectors) do send("grid repair module "..wm.aether.empath.table.collectors[key], false) end end if #wm.aether.empath.table.orb > 0 then send("grid repair module "..wm.aether.empath.table.orb[1], false) end if #aether.donext > 0 then send(aether.donext[1]) table.remove(aether.donext, 1) end
end

function wm.aether.turretBal()
if wm.aether.target ~= wm.aether.turret.target then send("turret target creature " ..wm.aether.target) else send("turret fire") end
end

function wm.aether.turret.makeList(enviro)

local types = {"void","ethereal","flux","storms","slipstream","celestial","nilspace","elemental","astral","godrealm",}

if table.contains(types, enviro) then
	if enviro == "void" then
		wm.aether.turret.list = {"scyllus", "dragon", "karibidean"}
	elseif enviro == "ethereal" then
		wm.aether.turret.list = {"gargantuan", "karibidean"}
	elseif enviro == "flux" then
		wm.aether.turret.list = {"pyrinnes", "grogogs", "serpent", "karibidean"}
	elseif enviro == "storms" then
		wm.aether.turret.list = {"hydrian", "cloier", "cloud", "karibidean"}
	elseif enviro == "slipstream" then
		wm.aether.turret.list = {"gruul", "lixin", "slanikk", "karibidean"}
	elseif enviro == "celestial" then
		wm.aether.turret.list = {"starbeast", "karibidean"}
	elseif enviro == "nilspace" then
		wm.aether.turret.list = {"shadow", "karibidean"}
	elseif enviro == "elemental" then
		wm.aether.turret.list = {"elemental", "karibidean"}
	elseif enviro == "astral" then
		wm.aether.turret.list= {"tendril", "karibidean"}
	elseif enviro == "godrealm" then
		wm.aether.turret.list= {"aebyr", "viaead", "guardian", "mannarast", "aesper", "molutoque", "spirit", "hamsa", "ufanq", "karibidean"}
	end
	wm.note("Environment set to <yellow>" ..enviro)
else
	wm.note("ERROR!!! Environment not properly set", "red")
	tempTimer(1, [[wm.note("Choices are <yellow>]]..table.concat(types,"<white>, <yellow>")..[[")]])
end

end
