Script.script:
if not loaded then loadVars() loaded = true end if not medic then medic = { ignore = {"aeon","insomnia","amnesia",}, pause = false, sipper = {health={},mana={},prio={"health","mana"},}, unknownThres = 3, } end if not medic.unknownThres then medic.unknownThres = 3 end remember("medic")
function medic.note(message, color) if not color then color = "green" end cecho("\n<blue>(<white>Medic!<blue>) <"..color..">"..message) end
function medic.reset() medic.stop = false medic.chaser.dochase = true medic.afflist = {} medic.unknown = 0 medic.sipper.bal = true medic.wounds = {head = 0, chest = 0, gut = 0, rarm = 0, larm = 0, rleg = 0, lleg = 0} medic.moss = true medic.note("System reset") end

medic.wounds = {head = 0, chest = 0, gut = 0, rarm = 0, larm = 0, rleg = 0, lleg = 0}
function medic.sipper.wounds(prio)
for part, num in pairs(medic.wounds) do if medic.sipper.bal == true then if part == "lleg" or part == "rleg" then part = "legs" elseif part == "larm" or part == "rarm" then part = "arms" end if num > 10000 then send("apply health to "..part) medic.sipper.bal = 2 tempTimer(1, [[if medic.sipper.bal == 2 then medic.sipper.bal = true end]]) end end end for part, num in pairs(medic.wounds) do if medic.sipper.bal == true then if part == "lleg" or part == "rleg" then part = "legs" elseif part == "larm" or part == "rarm" then part = "arms" end if num > 7000 then send("apply health to "..part) medic.sipper.bal = 2 tempTimer(1, [[if medic.sipper.bal == 2 then medic.sipper.bal = true end]]) end end end for part, num in pairs(medic.wounds) do if medic.sipper.bal == true then if part == "lleg" or part == "rleg" then part = "legs" elseif part == "larm" or part == "rarm" then part = "arms" end if num > 4000 then send("apply health to "..part) medic.sipper.bal = 2 tempTimer(1, [[if medic.sipper.bal == 2 then medic.sipper.bal = true end]]) end end end if prio == "high" then return else for part, num in pairs(medic.wounds) do if medic.sipper.bal == true then if part == "lleg" or part == "rleg" then part = "legs" elseif part == "larm" or part == "rarm" then part = "arms" end if num > 2000 then send("apply health to "..part) medic.sipper.bal = 2 tempTimer(1, [[if medic.sipper.bal == 2 then medic.sipper.bal = true end]]) end end end for part, num in pairs(medic.wounds) do if medic.sipper.bal == true then if part == "lleg" or part == "rleg" then part = "legs" elseif part == "larm" or part == "rarm" then part = "arms" end if num > 1000 then send("apply health to "..part) medic.sipper.bal = 2 tempTimer(1, [[if medic.sipper.bal == 2 then medic.sipper.bal = true end]]) end end end for part, num in pairs(medic.wounds) do if medic.sipper.bal == true then if part == "lleg" or part == "rleg" then part = "legs" elseif part == "larm" or part == "rarm" then part = "arms" end if num > 500 then send("apply health to "..part) medic.sipper.bal = 2 tempTimer(1, [[if medic.sipper.bal == 2 then medic.sipper.bal = true end]]) end end end for part, num in pairs(medic.wounds) do if medic.sipper.bal == true then if part == "lleg" or part == "rleg" then part = "legs" elseif part == "larm" or part == "rarm" then part = "arms" end if num > 100 then send("apply health to "..part) medic.sipper.bal = 2 tempTimer(1, [[if medic.sipper.bal == 2 then medic.sipper.bal = true end]]) end end end for part, num in pairs(medic.wounds) do if medic.sipper.bal == true then if part == "lleg" or part == "rleg" then part = "legs" elseif part == "larm" or part == "rarm" then part = "arms" end if num > 0 then send("apply health to "..part) medic.sipper.bal = 2 tempTimer(1, [[if medic.sipper.bal == 2 then medic.sipper.bal = true end]]) end end end end end

